In February 2020, I created a Mandelbrot set viewer. You can try it out here. (Click the black square. ) `code`<iframe srcdoc="<body style='overflow:hidden;margin:0'><div onclick='
this.parentNode.removeChild(this)
canv=document.createElement(&amp;quot;canvas&amp;quot;)
canv.width=600
canv.height=600
document.body.appendChild(canv)
rnc=canv.getContext(&amp;quot;webgl&amp;quot;)
rnc.clearColor(0,0,1,1)
rnc.clear(rnc.COLOR_BUFFER_BIT)

vsh=rnc.createShader(rnc.VERTEX_SHADER)
rnc.shaderSource(vsh,\t
precision mediump float;
attribute vec4 aVertexPosition;
uniform mat4 uModelViewMatrix;
uniform mat4 uProjectionMatrix;
varying vec4 pos;
void main(){
pos=aVertexPosition;
gl_Position=aVertexPosition;
}
\t)
rnc.compileShader(vsh)

fsh=rnc.createShader(rnc.FRAGMENT_SHADER)
rnc.shaderSource(fsh,\t
precision highp float;
varying vec4 pos;
uniform float anf;
uniform vec2 vct;
vec4 col(float iter){
return vec4(1,iter/65536.0,mod(iter,256.0)/256.0,1);
}
void main(){
vec2 tpos=pos.xy*2.0/anf+vct;
vec2 iteb=pos.xy*2.0/anf+vct;
gl_FragColor=vec4(0,0,0,1.0);
for(float ite=0.0;ite<2000.0;ite++){
iteb=vec2(iteb.x*iteb.x-iteb.y*iteb.y,2.0*iteb.x*iteb.y)+tpos;
if(iteb.x*iteb.x+iteb.y*iteb.y>4.0){
gl_FragColor=col(ite);
break;
}
}
}
\t)
rnc.compileShader(fsh)

prog=rnc.createProgram()
rnc.attachShader(prog,vsh)
rnc.attachShader(prog,fsh)
rnc.linkProgram(prog)
arb=new Float32Array([-1,-1,-1,1,1,1,1,-1])
rnc.bindBuffer(rnc.ARRAY_BUFFER,rnc.createBuffer())
rnc.bufferData(rnc.ARRAY_BUFFER,arb,rnc.STATIC_DRAW)
rnc.vertexAttribPointer(rnc.getAttribLocation(prog,&amp;quot;aVertexPosition&amp;quot;),2,rnc.FLOAT,false,0,0)
rnc.enableVertexAttribArray(rnc.getAttribLocation(prog,&amp;quot;aVertexPosition&amp;quot;))
rnc.useProgram(prog)

//vec2(-0.73581021291426529362666997258202173,0.19671833795849660853072293775767321)
gab=rnc.getUniformLocation(prog,&amp;quot;anf&amp;quot;)
vtg=rnc.getUniformLocation(prog,&amp;quot;vct&amp;quot;)
rnc.uniform1f(gab,1)
rnc.drawArrays(rnc.TRIANGLE_FAN,0,4)

k=1
cx=0
cy=0
zf=1.1
canv.onmousewheel=(e)=>{
e.preventDefault()
ev=e
if(e.deltaY<0){
	nx=(e.x-300)/150/k*(1-1/zf)
	ny=(300-e.y)/150/k*(1-1/zf)
	cx+=nx
	cy+=ny
	k*=zf
}else{
	k/=zf
}
rnc.uniform1f(gab,k)
rnc.uniform2f(vtg,cx,cy)
rnc.drawArrays(rnc.TRIANGLE_FAN,0,4)
}' style='background-color:#000000;width:100vw;height:100vh'></div></body>" width='600' height='600'></iframe>`
